import React, { useState } from 'react';
import './Login.css';
import axios from 'axios'; // Make sure you install axios
import Navbar from '../Navbar';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [userData, setUserData] = useState(null); // State to store user-specific data
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/api/auth/login', {
        email,
        password,
      });
      localStorage.setItem('token', response.data.token); // Store JWT token

      setMessage('Login successful!'); // Show success message
      console.log('Login successful:', response.data);

      // Fetch user-specific data after login
      const userDataResponse = await fetchUserData(response.data.token);
      setUserData(userDataResponse); // Save user data to state
      console.log('Fetched user data:', userDataResponse);

      navigate('/journal'); // Navigate to journal page
    } catch (error) {
      console.error('Login error:', error.response?.data);
      setMessage(error.response?.data || 'Login failed');
    }
  };

  const fetchUserData = async (token) => {
    try {
      const response = await axios.get('http://localhost:8080/api/user/data', {
        headers: {
          Authorization: `Bearer ${token}`, // Pass token for authentication
        },
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching user data:', error.response?.data || error.message);
      setMessage('Failed to fetch user data');
      throw error;
    }
  };

  return (
    <>
      <Navbar />
      <div className="login-container">
        <div className="login-card">
          <h2 className="login-title">Welcome Back!</h2>
          <form onSubmit={handleLogin}>
            <label>Email</label>
            <input
              type="email"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
            />
            <label>Password</label>
            <input
              type="password"
              value={password}
              required
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
            />
            <button type="submit" className="login-btn">Login</button>

            <p style={{ marginTop: '15px' }}>
              Don't have an account? <a href="/signup" style={{ color: '#007bff' }}>Sign up here</a>
            </p>
          </form>
          {userData && (
            <div className="user-data">
              <h3>Welcome, {userData.name}!</h3>
              <p>Email: {userData.email}</p>
              {/* Render more user-specific data here */}
            </div>
          )}
          {message && <p className="message">{message}</p>}
        </div>
      </div>
    </>
  );
};

export default Login;
